    //ngx-chart-curve-v3 折线
    create_broken_line(data, myChart, config) {
        var series = [];
        data.series.forEach((f, j) => {
            if (j == data.series.length - 1)
                series.push({
                    name: f.name,
                    type: 'line',
                    showSymbol: true,
                    lineStyle: {
                        width: 1
                    },
                    itemStyle: {
                        color: 'rgb(255, 70, 131)'
                    },
                    areaStyle: {
                        color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [{
                            offset: 0,
                            color: f.color[0]
                        }, {
                            offset: 1,
                            color: f.color[0]
                        }])
                    },
                    data: f.value
                });
            else
                series.push({
                    name: f.name,
                    type: 'line',
                    itemStyle: {
                        color: f.color[0]
                    },
                    lineStyle: {
                        width: 1
                    },
                    data: f.value
                });
        })
        if (series.length == 0) series.push({ type: 'line', name: '', data: [] });
        let option_e = {
            tooltip: {
                trigger: 'axis',
            },
            grid: {
                buttom: '0%',
                top: '5%',
                width: '90%',
                height: '70%'
            },
            dataZoom: [{
                    show: false,
                    realtime: true,
                    start: 100 - ((10 / (series[0].data.length)) * 100),
                    end: 100
                },
                {
                    type: 'inside',
                    realtime: true,
                    start: 100 - ((10 / (series[0].data.length)) * 100),
                    end: 100
                }
            ],
            xAxis: {
                data: data.xData,
                axisLabel: {
                    color: "white"
                },
            },
            yAxis: {
                splitLine: { show: false },
                min: 0,
                axisLabel: {
                    color: "white"
                },
            },
            series: series
        };
        myChart.setOption(option_e, config);
        // myChart.resize();
        window.onresize = function() {
            myChart.resize();
        }

    },

