    /**
     * 
     * @param name 名字
     * @param lines 具体数据
     * @param colors 颜色数组 长度2
     */
    create_series(name, lines, colors) {
        return {
            name: name,
            type: 'line',
            data: lines,
            lineStyle: {
                normal: {
                    width: 2,
                    // color: '#3374EB',
                    color: {
                        type: 'linear',
                        x: 0,
                        y: 0,
                        x2: 1,
                        y2: 0,
                        colorStops: [{
                            offset: 0,
                            color: colors[0] // 0% 处的颜色
                        }, {
                            offset: 1,
                            color: colors[1] // 100% 处的颜色
                        }],
                        global: false // 缺省为 false
                    },
                    shadowColor: colors[1],
                    shadowBlur: 4,
                    shadowOffsetY: 3
                }
            },
            symbol: 'emptyCircle',
            showSymbol: false,
            itemStyle: {
                normal: {
                    color: colors[1],
                    shadowColor: colors[1],
                    shadowBlur: 2,
                    borderWidth: 2,
                    borderColor: "#F8F8FF"
                }
            },
            smooth: true
        }
    },
    create_device_status_fun(data, seriesData, legend, borderData, borderHeight) {
        data.xData.forEach(element => {
            borderData.push(borderHeight);
        });
        data.d_arr.forEach((item, index) => {
            var obj1 = {};
            var obj2 = {};
            if (index < data.d_arr.length - 1) {
                obj1 = {
                    name: legend[index],
                    type: "bar",
                    stack: legend[index],
                    data: item,
                    barWidth: "15%",
                    itemStyle: {
                        normal: {
                            color: {
                                type: "linear",
                                x: 0,
                                y: 0,
                                x2: 0,
                                y2: 1,
                                colorStops: [{
                                        offset: 0,
                                        color: data.color_arr[index].start
                                    },
                                    {
                                        offset: 0.5,
                                        color: data.color_arr[index].start
                                    },
                                    {
                                        offset: 1,
                                        color: data.color_arr[index].end
                                    }
                                ],
                                globalCoord: false
                            }
                        }
                    }
                };
                obj2 = {
                    name: "",
                    type: "bar",
                    stack: legend[index],
                    itemStyle: {
                        normal: {
                            color: data.color_arr[index].start
                        }
                    },
                    data: borderData
                };
                seriesData.push(obj1);
                seriesData.push(obj2);
            } else {
                var obj3 = {
                    name: legend[index],
                    type: "line",
                    yAxisIndex: 1,
                    smooth: false,
                    symbol: "circle",
                    symbolSize: 10,
                    lineStyle: {
                        normal: {
                            width: 2
                        }
                    },
                    itemStyle: {
                        normal: {
                            color: data.color_arr[index].color,
                            borderColor: "#fff",
                            borderWidth: 1
                        }
                    },
                    data: item,
                    label: {
                        normal: {
                            show: false
                        }
                    }
                };
                seriesData.push(obj3);
            }
        });
    }
